mapper.insert:
INSERT
  INTO
       KH_BOARD
       (
       BOARD_NO
     , BOARD_TYPE
     , CATEGORY_NO
     , BOARD_TITLE
     , BOARD_CONTENT
     , BOARD_WRITER  
       )
VALUES
	   (
	   SEQ_BNO.NEXTVAL
	 , 1
	 , #{category}
	 , #{boardTitle}
	 , #{boardContent}
	 , #{boardWriter}  
	   )


	SELECT SEQ_BNO.NEXTVAL FROM DUAL

INSERT
  INTO
       KH_BOARD
       (
       BOARD_NO
     , BOARD_TYPE
     , CATEGORY_NO
     , BOARD_TITLE
     , BOARD_CONTENT
     , BOARD_WRITER  
       )
VALUES
	   (
	   #{boardNo}
	 , 1
	 , #{category}
	 , #{boardTitle}
	 , #{boardContent}
	 , #{boardWriter}  
	   )

INSERT
  INTO
       KH_ATTACHMENT
       (
       FILE_NO
     , REF_BNO
     , ORIGIN_NAME
     , CHANGE_NAME
     , FILE_PATH
       )
VALUES
       (
	   SEQ_FNO.NEXTVAL
	 , #{refBno}
	 , #{originName}
	 , #{changeName}
	 , #{filePath}
       )
